import React, { Component } from 'react'
import Calendar from './Calendar'
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";

class carInfo extends Component{
    state = {
        cars:[
            {name:"Mercedes AMG G63", src:"/images/rent-amg-g63.jpg",img1:"/images/AMG-1.jpg",img2:"/images/AMG-2.jpg",id:1},
            {name:"Lamborghini Aventador",src:"/images/rent-aventador.jpg",id:2},
            {name:"Porsche Cayman", src:"/images/rent-cayman.jpg",id:3},
            {name:"Lamborghini Urus", src:"/images/rent-urus.jpg",id:4},
            {name:"Lamborghini Hurucan",src:"/images/rent-hurucan.jpg",id:5},
            {name:"Ferrari 488",src:"/images/rent-ferrari-488.jpg",id:6},
            {name:"Bentley Bentayga",src:"/images/rent-bentayga.jpg",id:7},
            {name:"Mclaren 570 S",src:"/images/rent-mclaren.jpg",img:"/images/mclaren.jpg",id:8},
            {name:"Lamborghini Hurucan-White",src:"/images/rent-hurucan-white.jpg",id:9},
        ],
        car:'ff'
    }
    componentDidMount(){
        let id = this.props.match.params.car_id;
        let car = this.getcar(this.state.cars,id);
        console.log(car);
        // this.setState({ car: car }),
        // () => console.log(this.state)); 
        // console.log(this.state.car);
        // this.setState((car =>{
        //     return{
        //       car
        //     }
        //   }),()
        this.updateState(car);




    }
    updateState = (car) => {
         this.setState({
           car: car
         },() => { console.log('new state', this.state.car); })
       }
    render(){
        const settings={
            dots:true,
            speed:1500,
            autoplaySpeed: 3000,
            infinite:true,
            slidesToShow:1,
            slidesToScroll:1,
            arrows:true, 
            autoplay: true,
            className:"slides"
        };

        // let name = this.state.car.name
        return(
            <div className="container"> 
                <div className="center">
                    <h3>Rent a {this.state.car.name}</h3>
                </div>

                <div className="row">
                    <div className="col l6 xl6">
                        <div className="center">
                            <img src={this.state.car.src} alt=""/>
                            <h4>Available at $600/day </h4>
                        </div>

                    </div>
                    <div className="col l3 xl2">
                        <div className="center">
                        <img width="60%"src={this.state.car.img} alt=""/>
                        </div>


                    </div>

                </div>


            </div>
        );
    }

    getcar(cars,id){
        for (var i = 0; i < cars.length; i++) {
            if(cars[i].name==id){
                return(cars[i]);
            }
        }
    }

}
export default carInfo;
